mapper.insert:
INSERT INTO abizcar_person ( CO_CD, SEQ_NB, CAR_CD, USE_DT, EMP_CD, START_TIME, END_TIME, DEPT_CD, USE_FG, START_FG, START_ADDR, START_ADDR1, END_FG, END_ADDR, END_ADDR1, MILEAGE_KM, BEFORE_KM, AFTER_KM, SEND_YN, INSERT_ID, INSERT_IP, INSERT_DT, MODIFY_ID, MODIFY_IP, MODIFY_DT, RMK_DC
) VALUES ( #{co_cd}, #{seq_nb}, #{car_cd}, #{use_dt}, #{emp_cd}, #{start_time}, #{end_time}, #{dept_cd}, #{use_fg}, #{start_fg}, #{start_addr}, #{start_addr1}, #{end_fg}, #{end_addr}, #{end_addr1}, #{mileage_km}, #{before_km}, #{after_km}, #{send_yn}, #{insert_id}, #{insert_ip}, #{insert_dt}, #{modify_id}, #{modify_ip}, #{modify_dt}, #{rmk_dc}
)

mapper.update:
UPDATE abizcar_person SET
CO_CD = #{co_cd}, SEQ_NB = #{seq_nb}, CAR_CD = #{car_cd}, USE_DT = #{use_dt}, EMP_CD = #{emp_cd}, START_TIME = #{start_time}, END_TIME = #{end_time}, DEPT_CD = #{dept_cd}, USE_FG = #{use_fg}, START_FG = #{start_fg}, START_ADDR = #{start_addr}, START_ADDR1 = #{start_addr1}, END_FG = #{end_fg}, END_ADDR = #{end_addr}, END_ADDR1 = #{end_addr1}, MILEAGE_KM = #{mileage_km}, BEFORE_KM = #{before_km}, AFTER_KM = #{after_km}, SEND_YN = #{send_yn}, MODIFY_ID = #{modify_id}, MODIFY_IP = #{modify_ip}, MODIFY_DT= #{modify_dt}, RMK_DC = #{rmk_dc}
WHERE SEQ_NB = #{seq_nb} AND CAR_CD = #{car_cd}

delete.foreach:
DELETE FROM
abizcar_person
WHERE abizcar_person.SEQ_NB = #{record.seq_nb}
AND
abizcar_person.CAR_CD = #{record.car_cd}

mapper.update:
UPDATE abizcar_person SET
MILEAGE_KM = #{mileage_km},
AFTER_KM = BEFORE_KM + #{mileage_km}
WHERE CAR_CD = #{car_cd} AND
SEQ_NB = #{seq_nb}

mapper.select:
SELECT
COALESCE(STARTACC_KM ,0) FROM aper_startacc_info WHERE CAR_CD =
#{car_cd}

update.foreach:
UPDATE
abizcar_person
SET
abizcar_person.MILEAGE_KM= #{record.mileage_km}
WHERE abizcar_person.SEQ_NB = #{record.seq_nb} AND
abizcar_person.CAR_CD = #{record.car_cd}
